
public class Mathtest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    System.out.println(Math.sqrt(4));//计算平方根，返回double
    System.out.println(Math.pow(2,3));//计算幂，输入两个double，返回double
    System.out.println(Math.sin(60));
    System.out.println(Math.exp(69));//求e指数
    System.out.println(Math.log(69));//求log指数
    System.out.println(Math.abs(-40));//求绝对值
    System.out.println(Math.PI);//π值
    System.out.println(Math.E);//e值
    System.out.println(Math.round(6.66));//取整
	}

}
